
def hex_to_voltage(hex_values, adc_max=4095, v_ref=3.3):
    # Split the hex values into pairs of two bytes (16-bit values)
    adc_values = [int(hex_values[i:i+4], 16) for i in range(0, len(hex_values), 4)]
    
    # Convert each ADC value to voltage
    voltages = [(adc_value / adc_max) * v_ref for adc_value in adc_values]
    
    return voltages

# Example input data (from the packet you provided)
hex_data = ""

# Convert and print voltages
voltages = hex_to_voltage(hex_data)
for i, voltage in enumerate(voltages):
    print(f"ADC Value {i+1}: {voltage:.3f}V")
